import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [file, setFile] = useState(null);
  const [password, setPassword] = useState('');
  const [uploadMessage, setUploadMessage] = useState('');
  const [fileKey, setFileKey] = useState('');
  const [downloadPassword, setDownloadPassword] = useState('');
  const [downloadMessage, setDownloadMessage] = useState('');

  const handleUpload = async () => {
    if (!file || !password) return alert('Select a file and enter password.');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('password', password);

    try {
      const res = await axios.post('http://localhost:4000/upload', formData);
      setUploadMessage(res.data.message + '\nFile uploaded: ' + file.name);
    } catch (err) {
      setUploadMessage(err.response?.data?.error || 'Upload failed');
    }
  };

  const handleDownload = async () => {
    if (!fileKey || !downloadPassword) return alert('Enter file key and password.');

    try {
      const res = await axios.post('http://localhost:4000/download', {
        fileKey,
        password: downloadPassword
      }, { responseType: 'blob' });

      const blob = new Blob([res.data]);
      const link = document.createElement('a');
      link.href = window.URL.createObjectURL(blob);
      link.download = fileKey.replace('.enc', '');
      link.click();

      setDownloadMessage('Download and decryption successful.');
    } catch (err) {
      setDownloadMessage(err.response?.data?.error || 'Download failed');
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: 'auto', padding: 30, fontFamily: 'sans-serif' }}>
      <h2>🔐 Secure Decentralized Storage</h2>

      <h3>📤 Upload</h3>
      <input type="file" onChange={e => setFile(e.target.files[0])} /><br /><br />
      <input type="password" placeholder="Enter password" value={password} onChange={e => setPassword(e.target.value)} /><br /><br />
      <button onClick={handleUpload}>Encrypt & Upload</button>
      <p style={{ color: 'green' }}>{uploadMessage}</p>

      <hr />

      <h3>📥 Download</h3>
      <input type="text" placeholder="Enter uploaded file name" value={fileKey} onChange={e => setFileKey(e.target.value)} /><br /><br />
      <input type="password" placeholder="Enter password" value={downloadPassword} onChange={e => setDownloadPassword(e.target.value)} /><br /><br />
      <button onClick={handleDownload}>Decrypt & Download</button>
      <p style={{ color: 'blue' }}>{downloadMessage}</p>
    </div>
  );
};

export default App;
