import React, { useState } from 'react'
import axios from 'axios'

function App() {
  const [file, setFile] = useState(null)
  const [password, setPassword] = useState('')
  const [status, setStatus] = useState('')

  const encryptFile = async (file, password) => {
    const arrayBuffer = await file.arrayBuffer()
    const key = await window.crypto.subtle.importKey(
      'raw',
      new TextEncoder().encode(password.padEnd(32, '0').slice(0, 32)),
      { name: 'AES-GCM' },
      false,
      ['encrypt']
    )
    const iv = window.crypto.getRandomValues(new Uint8Array(12))
    const encrypted = await window.crypto.subtle.encrypt(
      { name: 'AES-GCM', iv },
      key,
      arrayBuffer
    )
    const combined = new Uint8Array(iv.length + encrypted.byteLength)
    combined.set(iv)
    combined.set(new Uint8Array(encrypted), iv.length)
    return new Blob([combined], { type: file.type })
  }

  const handleUpload = async () => {
    try {
      setStatus('Uploading...')
      const encryptedFile = await encryptFile(file, password)
      const formData = new FormData()
      formData.append('file', encryptedFile, file.name)

      await axios.post('http://localhost:5000/upload', formData)
      setStatus('‚úÖ Upload successful!')
    } catch (err) {
      setStatus('‚ùå Upload failed')
    }
  }

  return (
    <div style={{ padding: 30, fontFamily: 'Arial' }}>
      <h2>üîê Secure Decentralized Storage</h2>

      <h3>üì§ Upload</h3>
      <input type="file" onChange={e => setFile(e.target.files[0])} />
      <br />
      <input
        type="password"
        placeholder="Enter password"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />
      <br />
      <button onClick={handleUpload}>Encrypt & Upload</button>
      <p>{status}</p>

      <hr />

      <h3>üì• Download</h3>
      {/* You can implement download functionality later */}
    </div>
  )
}

export default App
